import React, { useEffect, useState } from "react";
import './RecentOpenings.css'
import { interndata } from "../../Data/data.js"
import { useNavigate } from 'react-router-dom';

export const RecentOpenings = () => {


  const navigate = useNavigate();

  function handleClick(option) {
    navigate(`/opportunity/${option.title}`, { state: { option } });
  }


  return (

      <div className='recent'>
      <div className="details">
            <div className='data'>
                {interndata.slice(0, 6).map((option, index) => {
                  return (
                    <div className="card" key={index}>
                            <div className="img">
                                <img src={option.img} className="img" onClick={() => handleClick(option)}/>
                            </div>
                            <div className='title' className='title' onClick={() => handleClick(option)}><b>{option.title}</b></div>
                            <div className="desc">Batch: {option.batch}</div>
                            <div className="desc"><b className="subhead">{option.type}:</b> {option.date}</div>
                        </div>
                    );
                  })}
            </div>
            <div className="btn"><a href='/opportunity'>Show More</a></div>
        </div>

    </div>
  );


}
