    const location = useLocation();
    const { option } = location.state;
return (
    <>
        <div className="opprotunityInfo">
            {/* <div className="image"> */}
            <img src={option.img}/>
            {/* </div> */}
            <div className="date">Posted On: {option.date}</div>
            <div className=""></div>
        </div>
    </>
)
};
